/**
 * Plugin: Upload and insert image plugin, by Jason Thompson - http://hondo.elance.com
 * 
 * A plugin to upload an image from the user's computer and insert it into the document
 * 
 */

(function() {
	tinymce.create('tinymce.plugins.UploadAndInsertPlugin', {
		/* This will cache the items in the dropdown */
		
		uploadURL:'',
		input:null,
		
		init : function(ed, url) {
			var self = this;
			this.editor = ed;
			// Register commands
			ed.addCommand('mceAdvLink', function() {
				ed.windowManager.open({
					file : url + '/link.htm',
					width : 480 + parseInt(ed.getLang('advlink.delta_width', 0)),
					height : 400 + parseInt(ed.getLang('advlink.delta_height', 0)),
					inline : 1
				}, {
					plugin_url : url
				});

			});

			// Register buttons
			
			ed.addButton('uploadandinsert', {
				title : 'Upload an image and insert it',
				cmd : 'mceAdvLink',
				image:'assets/js/tinymce/js/tinymce/plugins/uploadandinsert/images/uploadImg.png'
			});
			
			
			//ed.addShortcut('ctrl+k', 'advlink.advlink_desc', 'mceAdvLink');

			ed.onNodeChange.add(function(ed, cm, n, co) {
				cm.setDisabled('link', co && n.nodeName != 'A');
				cm.setActive('link', n.nodeName == 'A' && !n.name);
			});
		},

		getInfo : function() {
			return {
				longname : 'Upload and Insert Image',
				author : 'Moxiecode Systems AB, modified by Jason Thompson',
				authorurl : 'http://tinymce.moxiecode.com and http://hondo.elance.com',
				infourl : '',
				version : tinymce.majorVersion + "." + tinymce.minorVersion
			};
		}
	});

	// Register plugin
	tinymce.PluginManager.add('uploadandinsert', tinymce.plugins.UploadAndInsertPlugin);
})();